import React, { useState }  from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import {Container, Row, Col, Form, Button} from 'react-bootstrap';
import axios from 'axios'



const AddContact = () => {

    const [contact, setContact] = useState({
        "name": "",
        "contact_number": ""
    });
    ;

    const saveContact = (contact) =>{
    
        axios.post(
          'http://127.0.0.1:8000/api/contacts/', {
          "name": contact.name,
          "contact_number": contact.contact_number
        }).then(function (response) {
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });
      }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(contact);
        saveContact(contact);
        
    }

    return (
        // <h1>Hey</h1>
        <div className="#">
             <Container className="justify-content-center" col="4" fluid>
             <Row>
                <Col md={{ span: 4, offset: 4 }}>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3" controlId="formFriend">
                            
                        <Form.Label>Friend Name</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Enter Your Friend's Name" 
                            name="name"
                            onChange= {(e) => setContact({...contact, name: e.target.value})}/>

                        <Form.Label>Contact Number</Form.Label>
                        <Form.Control 
                            type="number" 
                            placeholder="Friend's Contact Number"
                            name="contactNumber" 
                            onChange= {(e) => setContact({...contact, contact_number: e.target.value})} />

                        </Form.Group>

                        <Button variant="primary" type="submit">Submit</Button>
                    </Form>
                </Col>
                </Row>
            </Container>
        </div>

    )
}

export default AddContact
