          {contacts.map((contact) => {
       return <p>{contact.contact_number}</p>      
     })}</Col>
        </Row>
        {/* <Fab color="primary" aria-label="add">
  <AddIcon />
</Fab> */} 

      {/* </Container> */}
      {/* <button onClick={saveContact}>Add Number</button> */}
      {/* <Button variant="primary">Add<A href="/add-contact">j</A></Button>{' '} */}
      {/* <A href="/contact">Contact List</A> <br /> */}
      <A href="/add-contact">AddContact</A> <br />

      <AddContact />
      {routeResult}
    </div>
  );
}

export default App;
