import 'bootstrap/dist/css/bootstrap.css';
import {Container, Row, Col, Form, Button} from 'react-bootstrap';
import { useForm } from "react-hook-form";



const AddContact = ({name, contactNumber}) => {
    return (
        // <h1>Hey</h1>
        <div className="#">
             <Container className="justify-content-center" col="4" fluid>
             <Row>
                <Col md={{ span: 4, offset: 4 }}>
                    {/* <Form>
                        <Form.Group className="mb-3" controlId="formFriend">
                            
                        <Form.Label>Friend Name</Form.Label>
                        <Form.Control type="text" placeholder="Enter Your Friend's Name" />

                        <Form.Label>Contact Number</Form.Label>
                        <Form.Control type="number" placeholder="Friend's Contact Number" />

                        </Form.Group>

                        <Button variant="primary" type="submit">Submit</Button>
                    </Form> */}
                        <form >
      {/* register your input into the hook by invoking the "register" function */}
      <input defaultValue="test" {...register("example")} />
      
      {/* include validation with required or other standard HTML validation rules */}
      <input {...register("exampleRequired", { required: true })} />
      {/* errors will return when field validation fails  */}
      {errors.exampleRequired && <span>This field is required</span>}
      
      <input type="submit" />
    </form>
                </Col>
                </Row>
            </Container>
        </div>

    )
}

export default AddContact
